import React from "react";
import { useSelector } from 'react-redux'
// Components
import ConfigLeft from "../fileConfiguration/ConfigLeft";
import ConfigRight from "../fileConfiguration/ConfigRight";

const MainWindow = () => {
  const company = useSelector((state) => {
    return state.sidebarReducer
  });

  return (
    <div className={company.sidebarOpen?'mainWindow configMainContent d-flex justify-content-between sidebarOpened':'mainWindow configMainContent d-flex justify-content-between sidebarClosed'}>
              <div className="configLeftHead d-flex align-items-center">
          <h5 className="fontWeight-600 fileConfigHead colorBlack">
            File Configuration
          </h5>
        </div>
      <ConfigLeft />
      <ConfigRight />
    </div>
  );
};

export default MainWindow;
